import React, {Component} from 'react';
import { BrowserRouter as Router,Route} from 'react-router-dom';
import UserPosts from './components/UserPosts';
import AddTodo from './components/AddTodo';
import Photos from './components/Photos';
import Header from './components/layout/Header';
import About from './components/pages/About';
import './App.css';
import axios from 'axios';

class  App extends Component {
  state ={
    userposts:[],
	photos:[]
  }

  componentDidMount(){
    axios.get('https://jsonplaceholder.typicode.com/posts?_limit=5').then(
      res => this.setState({userposts:res.data}))

	axios.get('https://jsonplaceholder.typicode.com/photos?_limit=10').then(
      res => this.setState({photos:res.data}))
  }



  //Toggle Complete
  markComplete =(id) =>{
    this.setState({todos:this.state.todos.map(todo =>{
      if(todo.id == id){
        todo.completed = !todo.completed
      }
      return todo;
    })});
}

  //Toggle Complete
  delTodo =(id) =>{
    axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`).then(res=>
    this.setState({todos: [...this.state.todos.filter(todo => todo.id !== id)]})); 
}

addTodo =(title,body) =>{
  axios.post('https://jsonplaceholder.typicode.com/posts',{
  title,
  body
 }).then(res => this.setState({todos:[...this.state.todos,res.data]}))

}

  render(){

        return (
          <Router>
              <div className="App">
              <div className="container">
                <Header/>
                <Route exact path="/" render={props =>(
                  <React.Fragment>
                      <AddTodo addTodo={this.addTodo}/>
                      <UserPosts userposts ={this.state.userposts} 
                        markComplete={this.markComplete}
                        delTodo={this.delTodo}/>   
						          <Photos photos ={this.state.photos} />
                  </React.Fragment>
                )}/>  


                 <Route exact path="/about" component={About} />    
              </div>
             </div>
          </Router>   
    );
  }
}

export default App;
